import { Component, inject, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-compounds',
  templateUrl: './compounds.component.html',
  styleUrls: ['./compounds.component.css']
})
export class CompoundsComponent implements OnInit {
  Math = Math;

  private http = inject(HttpClient);
  private route = inject(ActivatedRoute);
  private router = inject(Router);
  private authService = inject(AuthService);

  compounds: Compound[] = [];
  isLoading = true;
  errorMessage = '';
  page = 1;
  total = 0;
  pageSize = 10; // Assume page size (update if different)

  ngOnInit() {
    this.route.queryParams.subscribe(params => {
      this.page = +params['page'] || 1;
      this.fetchCompounds();
    });
  }

  fetchCompounds() {
    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    this.isLoading = true;
    this.http.get<{ data: Compound[], total: number }>(
      `http://localhost:3000/compounds?page=${this.page}`,
      { headers }
    ).subscribe({
      next: (res) => {
        this.compounds = res.data;
        this.total = res.total;
        this.isLoading = false;
      },
      error: (err) => {
        this.errorMessage = err.error?.message || 'Failed to load compounds';
        this.isLoading = false;
      }
    });
  }

  prevPage() {
    if (this.page > 1) {
      this.page--;
      this.updateUrlAndFetch();
    }
  }

  nextPage() {
    // Calculate max page count
    const maxPage = Math.ceil(this.total / this.pageSize);
    if (this.page < maxPage) {
      this.page++;
      this.updateUrlAndFetch();
    }
  }

  updateUrlAndFetch() {
    // Update URL query params and fetch compounds for new page
    this.router.navigate([], {
      relativeTo: this.route,
      queryParams: { page: this.page },
      queryParamsHandling: 'merge',
    });
    this.fetchCompounds();
  }

  goToCompound(id: number) {
    this.router.navigate(['/compound', id]);
  }
}

interface Compound {
  id: number;
  name: string;
  image: string;
  description: string;
}
<div *ngIf="isLoading" class="loading">Working on the page...</div>

<div *ngIf="errorMessage" class="error">
  {{ errorMessage }}
</div>

<div *ngIf="!isLoading && compounds.length > 0" class="page">
  <div class="cards-container">



  <div
    *ngFor="let compound of compounds"
    class="compound-card"
    (click)="goToCompound(compound.id)"
  >
    <img
    class=""
    [src]="compound.image"
    alt="{{ compound.name }}"
    width="200"
    height="200"

    />  
    <h2 class="card-name">{{compound.name}}</h2>
  </div>
</div>
 <!-- Pagination buttons -->
  <div class="pagination-buttons" style="margin-top: 1rem; text-align: center;">
    <button mat-raised-button color="primary" (click)="prevPage()" [disabled]="page === 1">
      Previous
    </button>
    <button mat-raised-button color="primary" (click)="nextPage()" [disabled]="page >= Math.ceil(total / pageSize)">
      Next
    </button>
  </div>
</div>


<div *ngIf="!isLoading && compounds.length === 0">
  No compounds found.
</div>
