import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup } from '@angular/forms';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-edit-compound',
  templateUrl: './edit-compound.component.html',
  styleUrls: ['./edit-compound.component.css']
})
export class EditCompoundComponent implements OnInit {
  form!: FormGroup;
  id!: string;

  constructor(
    private fb: FormBuilder,
    private http: HttpClient,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit() {
    this.id = this.route.snapshot.paramMap.get('id')!;
    this.form = this.fb.group({
      name: [''],
      image: [''],
      description: ['']
    });

    const headers = new HttpHeaders().set('Authorization', `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9...`);

    this.http.get<any>(`http://localhost:3000/compounds/${this.id}`, { headers }).subscribe(data => {
      this.form.patchValue(data);
    });
  }

  onSubmit() {
    const headers = new HttpHeaders().set('Authorization', `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9...`);
    this.http.put(`http://localhost:3000/compounds/${this.id}`, this.form.value, { headers }).subscribe(() => {
      this.router.navigate(['/compounds', this.id]);
    });
  }
}
