import { Component, inject, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-compounds',
  templateUrl: './compounds.component.html',
  styleUrls: ['./compounds.component.css']
})
export class CompoundsComponent implements OnInit {
  private http = inject(HttpClient);
  private route = inject(ActivatedRoute);
  private router = inject(Router);

  compounds: Compound[] = [];
  isLoading = true;
  errorMessage = '';
  page = 1;
  total = 0;

  ngOnInit() {
    this.route.queryParams.subscribe(params => {
      this.page = +params['page'] || 1;
      this.fetchCompounds();
    });
  }

  fetchCompounds() {
    const token = `eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOjEsImlhdCI6MTc0ODEwNjQyMCwiZXhwIjoxNzQ4MTEwMDIwfQ.alBB5_NrR35f7xHZMZGgOtWlksI_QQPJW_7uJXRBNgE`;
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    this.isLoading = true;
    this.http.get<{ data: Compound[], total: number }>(
      `http://localhost:3000/compounds?page=${this.page}`,
      { headers }
    ).subscribe({
      next: (res) => {
        this.compounds = res.data;
        this.total = res.total;
        this.isLoading = false;
      },
      error: (err) => {
        this.errorMessage = err.error?.message || 'Failed to load compounds';
        this.isLoading = false;
      }
    });
  }

  goToCompound(id: number) {
    this.router.navigate(['/compound', id]);
  }
}

interface Compound {
  id: number;
  name: string;
  image: string;
  description: string;
}
<div *ngIf="isLoading" class="loading">Working on the page...</div>

<div *ngIf="errorMessage" class="error">
  {{ errorMessage }}
</div>

<div *ngIf="!isLoading && compounds.length > 0" class="card-container">
  <div
    *ngFor="let compound of compounds"
    class="compound-card"
    (click)="goToCompound(compound.id)"
  >
    <img [src]="compound.image" alt="{{ compound.name }}" />
    <h3>{{ compound.name }}</h3>
    <p>{{ compound.description }}</p>
  </div>
</div>

<div *ngIf="!isLoading && compounds.length === 0">
  No compounds found.
</div>
